/*

Your task is to create a fence worth $1 million. You are given the price of the material (per character), meaning the length of the fence will change depending on the cost of the material.

Create a function which constructs this pricey pricey fence, using the letter 'H' to build.

constructFence('$50,000') ➞ 'HHHHHHHHHHHHHHHHHHHHHHHHHHHH'
// 20 fence posts were set up ($1,000,000 / $50,000 = 20)

Examples

constructFence('$50,000') ➞ 'HHHHHHHHHHHHHHHHHHHHHHHHHHHH'

constructFence('$100,000') ➞ 'HHHHHHHHHH'

constructFence('$1,000,000') ➞ 'H'

Notes

    You are ordered to spend all of your $1,000,000 budget...

*/

package main

import (
	"strconv"
	"strings"
)

func main() {
	assert(fence("$50,000") == "HHHHHHHHHHHHHHHHHHHH")
	assert(fence("$100,000") == "HHHHHHHHHH")
	assert(fence("$1,000,000") == "H")
	assert(fence("$500,000") == "HH")
	assert(fence("$20,000") == "HHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHH")
	assert(fence("$10,000") == "HHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHH")
	assert(fence("$5000") == "HHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHH")
	assert(fence("$1000") == "")
}

func fence(s string) string {
	s = strings.Replace(s, "$", "", -1)
	s = strings.Replace(s, ",", "", -1)
	n, _ := strconv.Atoi(s)
	if n == 0 {
		return ""
	}
	return strings.Repeat("H", 1000000/n)
}

func assert(x bool) {
	if !x {
		panic("assertion failed")
	}
}
